const input = $json.raw_input;
const parts = input.split(',');

return [{
  id: Date.now(), // auto-generate ID
  category: parts[0].trim(),
  title: parts[1].trim(), 
  data_json: { content: parts[2].trim() },
  tags: parts[3] ? parts[3].split('|') : [],
  embedding: parts[4] ? parts[4].split('|').map(n => parseFloat(n.trim())) : [0],
  is_sensitive: parts[5] === 'true',
  created_at: new Date().toISOString(),
  updated_at: new Date().toISOString()
}];
