const items = $input.all();
const processedItems = [];

for (const item of items) {
  const row = item.json;

  // Clean and structure data
  const cleanedItem = {
    category: row.category.trim(),
    title: row.title.trim(), 
    content: row.content.trim(),
    tags: row.tags ? row.tags.split(';').map(tag => tag.trim()) : [],
    is_sensitive: row.is_sensitive === 'true',
    source_row: processedItems.length + 1
  };

  processedItems.push(cleanedItem);
}

console.log(`Processed ${processedItems.length} items`);
return processedItems.map(item => ({ json: item }));

const items = $input.all();
const chunks = [];
const CHUNK_SIZE = 400;
const OVERLAP_SIZE = 50;

function createChunks(text, metadata) {
  if (text.length <= CHUNK_SIZE) {
    return [{
      text: text,
      chunk_index: 0,
      total_chunks: 1,
      ...metadata
    }];
  }

  const textChunks = [];
  let start = 0;
  let chunkIndex = 0;

  while (start < text.length) {
    let end = Math.min(start + CHUNK_SIZE, text.length);
    const chunkText = text.slice(start, end).trim();

    if (chunkText.length > 0) {
      textChunks.push({
        text: chunkText,
        chunk_index: chunkIndex,
        total_chunks: 0,
        ...metadata
      });
      chunkIndex++;
    }

    start = Math.max(end - OVERLAP_SIZE, start + 1);
  }

  textChunks.forEach(chunk => chunk.total_chunks = textChunks.length);
  return textChunks;
}

for (const item of items) {
  const data = item.json;
  const chunkList = createChunks(data.content, {
    category: data.category,
    title: data.title,
    tags: data.tags,
    is_sensitive: data.is_sensitive,
    source_row: data.source_row
  });

  chunks.push(...chunkList);
}

console.log(`Created ${chunks.length} chunks from ${items.length} documents`);
return chunks.map(chunk => ({ json: chunk }));

