// Parse CSV output from AI Agent
const csvRow = $json.output || $json.text || $json.response;
console.log('AI Output:', csvRow);

// Simple CSV parser for our specific format
const parseCSV = (csvString) => {
  const parts = [];
  let current = '';
  let inQuotes = false;

  for (let i = 0; i < csvString.length; i++) {
    const char = csvString[i];

    if (char === '"') {
      inQuotes = !inQuotes;
    } else if (char === ',' && !inQuotes) {
      parts.push(current.trim());
      current = '';
    } else {
      current += char;
    }
  }
  parts.push(current.trim());

  return parts;
};

const parsed = parseCSV(csvRow);

return [{
  json: {
    category: parsed[0] || 'personal_data',
    title: parsed[1]?.replace(/"/g, '') || 'Unknown Document',
    content: parsed[2]?.replace(/"/g, '') || 'No content',
    tags: parsed[3]?.split(';').map(tag => tag.trim()) || ['unknown'],
    is_sensitive: parsed[4] === 'true',
    raw_csv: csvRow,
    timestamp: new Date().toISOString()
  }
}];

