// Handle file upload and determine processing method
const files = $input.first().binary || {};
const body = $input.first().json.body || {};

const fileKeys = Object.keys(files);
const firstFile = fileKeys.length > 0 ? files[fileKeys[0]] : null;

if (firstFile) {
  const fileName = firstFile.fileName || 'unknown';
  const mimeType = firstFile.mimeType || 'unknown';
  const fileExtension = fileName.split('.').pop()?.toLowerCase();

  return [{
    json: {
      file_name: fileName,
      mime_type: mimeType,
      file_extension: fileExtension,
      processing_method: getProcessingMethod(fileExtension),
      upload_timestamp: new Date().toISOString(),
      user_id: body.user_id || 'anonymous'
    },
    binary: {
      data: firstFile
    }
  }];
} else {
  // Handle direct text input
  return [{
    json: {
      file_name: 'direct_input.txt',
      mime_type: 'text/plain',
      file_extension: 'txt',
      processing_method: 'direct_text',
      content: body.text || body.content || '',
      upload_timestamp: new Date().toISOString(),
      user_id: body.user_id || 'anonymous'
    }
  }];
}

function getProcessingMethod(extension) {
  switch(extension) {
    case 'pdf': return 'pdf_extract';
    case 'docx': case 'doc': return 'word_extract';
    case 'txt': return 'direct_text';
    case 'jpg': case 'jpeg': case 'png': return 'ocr_extract';
    case 'eml': return 'email_extract';
    default: return 'unknown';
  }
}
